<script>
	// Replace 'base64AudioString' with your actual Base64-encoded audio string
	const base64AudioString =
		'';

	// Convert the Base64 string to a Uint8Array
	const binaryString = window.atob(base64AudioString);
	const binaryData = new Uint8Array(binaryString.length);

	for (let i = 0; i < binaryString.length; i++) {
		binaryData[i] = binaryString.charCodeAt(i);
	}

	// Create a Blob from the Uint8Array
	const blob = new Blob([binaryData], { type: 'audio/mpeg' }); // Adjust the MIME type accordingly

	// Now 'blob' contains your audio data in Blob format

	let audio;
	let bloburl1;

	function playAudio() {
		const blobUrl = URL.createObjectURL(blob);
		bloburl1 = blobUrl;
		audio.src = blobUrl;
		audio.play();
		console.log(blob);
	}

	function releaseBlobUrl() {
		if (audio) {
			audio.pause();
			audio.src = '';
		}
		URL.revokeObjectURL(audio.src);
	}

	function downloadAudio(bloburl1) {
		const a = document.createElement('a');
		a.href = bloburl1;
		a.download = 'audio2.mp3'; // You can set the desired filename and extension
		document.body.appendChild(a);
		a.click();
		document.body.removeChild(a);
	}
</script>

<div>
	<audio bind:this={audio} on:ended={releaseBlobUrl}></audio>
	<button on:click={playAudio}>Play Audio</button>
	<button on:click={() => downloadAudio(bloburl1)}>Download Audio</button>
</div>
<p>The Blob URL is: {bloburl1}</p>

<style>
	:global(a) {
		width: 100px;
		height: 100px;
		background: green;
		display: block;
	}
</style>
